#!/bin/bash
#Imports

.  ./f_run.sh

# Import commonconfig
if [ ! -e masterscripts/commonconfig.sh ]
then
    echo "<commonconfig.sh> missing"
    exit 1
else
.  ./masterscripts/commonconfig.sh
fi
 
# This script runs a single test case or all test cases
# The tests can be run with ARM simulator or executable binary
# For simulator, the input is ASIC reference data generated by system model

mount_point=/export # htis is for versatile
if [ ! -e $mount_point ]; then
    mount_point=/mnt/export # htis is for integrator
fi

#SET THIS
# HW burst size 0..63
burst=16

enc_bin=./mpeg4_testenc
if [ ! -e $enc_bin ]; then
    echo "Executable $enc_bin not found. Compile with 'make'"
    exit
fi

if [ ! -e $test_case_list_dir/test_data_parameter_mpeg4.sh ]; then
    echo "<test_data_parameter_mpeg4.sh> not found"
    echo "$test_case_list_dir/test_data_parameter_mpeg4.sh" 
    exit -1
fi

# Output file when running all cases
# .log and .txt are created
resultfile=results_mpeg4

# Current date
#date=`date +"%d.%m.%y %k:%M"`

# Check parameter
if [ $# -eq 0 ]; then
    echo "Usage: test_mpeg4.sh <test_case_number>"
    echo "   or: test_mpeg4.sh <all>"
    echo "   or: test_mpeg4.sh <first_case> <last_case>"
    exit
fi

# Encoder command
# For armsd-simulator the command line must be less than 300 characters
# The userdata parameters should be commented out to achieve that
encode() {

    ${enc_bin} \
    -i ${input[${set_nro}]} \
    -a${firstVop[${set_nro}]} \
    -b${lastVop[${set_nro}]} \
    -w${lumWidthSrc[${set_nro}]} \
    -h${lumHeightSrc[${set_nro}]} \
    -x${width[${set_nro}]} \
    -y${height[${set_nro}]} \
    -X${horOffsetSrc[${set_nro}]} \
    -Y${verOffsetSrc[${set_nro}]} \
    -f${outputRateNumer[${set_nro}]} \
    -F${outputRateDenom[${set_nro}]} \
    -j${inputRateNumer[${set_nro}]} \
    -J${inputRateDenom[${set_nro}]} \
    -S${scheme[$set_nro]} \
    -p${profile[$set_nro]} \
    -k${videoRange[$set_nro]} \
    -I${intraVopRate[${set_nro}]} \
    -W${goVopRate[${set_nro}]} \
    -V${vpSize[${set_nro}]} \
    -D${dataPart[${set_nro}]} \
    -R${rvlc[${set_nro}]} \
    -E${hec[${set_nro}]} \
    -G${gobPlace[${set_nro}]} \
    -B${bitPerSecond[${set_nro}]} \
    -U${vopRc[${set_nro}]} \
    -u${mbRc[${set_nro}]} \
    -v${videoBufferSize[${set_nro}]} \
    -s${vopSkip[${set_nro}]} \
    -q${qpHdr[${set_nro}]} \
    -n${qpMin[${set_nro}]} \
    -m${qpMax[${set_nro}]} \
    -l${inputFormat[${set_nro}]} \
    -O${colorConversion[${set_nro}]} \
    -r${rotation[${set_nro}]} \
    -z${userDataVos[${set_nro}]} \
    -c${userDataVisObj[${set_nro}]} \
    -d${userDataVol[${set_nro}]} \
    -g${userDataGov[${set_nro}]} \
    -Z${stabilization[${set_nro}]} \
    -e${testId[${set_nro}]} \
    -N${burst} \
    -P$trigger         
}        

