            startTimeCounter "$case_nro"
            encode > encoder_mpeg4.log
            endTimeCounter "$case_nro"
	    
	    cat encoder_mpeg4.log
	    
	    if [ -e random_run ]
	    then
	        mv stream.mpeg4 random_cases/case_$case_nro.mpeg4
		touch random_cases/run_${case_nro}_done
		mv case_${case_nro}.time random_cases/case_${case_nro}.time
	    else
                mv stream.mpeg4 case_$case_nro.mpeg4
		touch run_${case_nro}_done
            fi
            
	    # Copy mb offset trace to different name 

